package 반복문;

public class ifelse_example {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int score = 89;
        
        if(score>=90) {
        	System.out.println("점수가 큽니다");
        	System.out.println("잘했습니다!");
        }else{
	        System.out.println("점수가 낮습니다");
	        System.out.println("불합격입니다!");
        	
        }
	}

}
